import React, { useState } from "react";

const foremen = [
  "Adrian McHardy", "Allan Crowfoot", "Brandon Wilson", "Brent Heyd", "Edward Walt",
  "Erik Turnbull", "Glenn Bissett", "Jeff Ball", "Jeff Tull", "Ken Kory",
  "Konnor Chelkowski", "Tom Inman", "Tyler Swanson"
];

const employees = [
  "Adrian McHardy", "Allan Crowfoot", "Andrew Smith", "Ben Swan", "Brandon Wilson",
  "Brent Heyd", "Charlie Williams", "Cole Sather", "Draven Cowan", "Edward Walt",
  "Erik Turnbull", "Ezra Oucharek", "Glenn Bissett", "Jeff Ball", "Jeff Tull",
  "John Kaiser", "John Yildiz (Muhammet)", "Jordan Kaiser", "Ken Kory",
  "Konnor Chelkowski", "Matreno Bajao", "Serdal Muslu", "Tom Inman", "Tyler Swanson"
];

function App() {
  const [schedule, setSchedule] = useState([]);
  const [formData, setFormData] = useState({
    foreman: "",
    helpers: ["", "", ""],
    location: ""
  });

  const handleSubmit = () => {
    const newEntries = [
      { name: formData.foreman, role: "Foreman", location: formData.location },
      ...formData.helpers.filter(h => h).map(h => ({
        name: h,
        role: "Helper",
        location: formData.location
      }))
    ];
    setSchedule([...schedule, ...newEntries]);
    setFormData({ foreman: "", helpers: ["", "", ""], location: "" });
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>Flynn Scheduler</h2>
      <div style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
        <select
          value={formData.foreman}
          onChange={(e) => setFormData({ ...formData, foreman: e.target.value })}
        >
          <option value="">Select Foreman</option>
          {foremen.map((e) => (
            <option key={e} value={e}>{e}</option>
          ))}
        </select>
        {formData.helpers.map((h, idx) => (
          <select
            key={idx}
            value={h}
            onChange={(e) => {
              const updated = [...formData.helpers];
              updated[idx] = e.target.value;
              setFormData({ ...formData, helpers: updated });
            }}
          >
            <option value="">Select Helper {idx + 1}</option>
            {employees.map((e) => (
              <option key={e} value={e}>{e}</option>
            ))}
          </select>
        ))}
        <input
          type="text"
          placeholder="Location"
          value={formData.location}
          onChange={(e) => setFormData({ ...formData, location: e.target.value })}
        />
        <button onClick={handleSubmit}>Add</button>
      </div>

      <div>
        {schedule.map((entry, idx) => (
          <div key={idx} style={{ padding: "5px 0", borderBottom: "1px solid #ccc" }}>
            <strong>{entry.role}:</strong> {entry.name} â€” <em>{entry.location}</em>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
